package sample.Models;import javafx.collections.FXCollections;import javafx.collections.ObservableList;import java.util.UUID;/** * Created by Fabrice on 19.06.2017. */public class Kino {    private UUID id;    private String name;    private String mitgliedsname;    private String email;    private String adresse;    public Kino(String id, String name, String mitgliedsname, String email, String adresse) {        this.id = UUID.fromString(id);        this.name = name;        this.mitgliedsname = mitgliedsname;        this.email = email;        this.adresse = adresse;    }    public Kino(String name, String mitgliedsname, String email, String adresse) {        this.id = UUID.randomUUID();        this.name = name;        this.mitgliedsname = mitgliedsname;        this.email = email;        this.adresse = adresse;    }    public String getID() {        return id.toString();    }}